import React,{ useState } from "react";

import "./pricing.css";

const Pricing = () => {
  const [isYearly, setIsYearly] = useState(false);

  const handleToggle = () => {
    setIsYearly(!isYearly);
  };
  const plans = {
    starter: {
      monthly: {
        price: "499",
        reports: "Up to 100 reports",
      },
      yearly: {
        price: "999",
        reports: "Up to 1000 reports",
      },
    },
    pro: {
      monthly: {
        price: "999",
        reports: "Up to 300 reports",
      },
      yearly: {
        price: "9,999",
        reports: "Up to 5000 reports",
      },
    },
    business: {
      monthly: {
        price: "1,999",
        reports: "Up to 1000 reports",
      },
      yearly: {
        price: "19,999",
        reports: "Unlimited reports",
      },
    },
  };

  return (
    <div className="llm__pricing section__padding" id="pricing">
      <div className="llm__pricing-heading">
        <h1>Pricing</h1>
      </div>
      <div className="llm__pricing-content">
        <div className="llm__pricing-content-heading">
          <h2>Choose a Plan That Fits Your Needs</h2>
          <h2>Flexible Pricing Options for Businesses of All Sizes</h2>
        </div>
        <div className="llm__pricing-content-plans">
        <div className="toggle-container">
        <span>Monthly</span>
        <label className="switch">
          <input type="checkbox" onChange={handleToggle} checked={isYearly} />
          <span className="slider round"></span>
        </label>
        <span>Yearly</span>
      </div>
          <div className="llm__pricing-content-plans-starter">
          <div className="llm__pricing-content-plans-starter">
        <h2>Starter</h2>
        <p>{isYearly ? plans.starter.yearly.price : plans.starter.monthly.price}</p>
        <p>{isYearly ? plans.starter.yearly.reports : plans.starter.monthly.reports}</p>
        {/* Additional details for Starter plan */}
      </div>

            <a href="http://main.episum.com/register/form">
              <button type="button">Create Free</button>
            </a>
          </div>

          {/* <div className="llm__pricing-content-plans-pro">
            <h2>Pro</h2>
            <button type="button">Create Free</button>
          </div> */}
          {/* <div className="llm__pricing-content-plans-teamPlans">
            <h2>Team Plans</h2>
            <button type="button">  Let's Talk   </button>
          </div> */}
        </div>
      </div>


    </div>
  );
};

export default Pricing;
