import React,{ useState } from "react";

import "./pricing.css";

const Pricing = () => {
  const [isYearly, setIsYearly] = useState(false);

  const handleToggle = () => {
    setIsYearly(!isYearly);
  };

  return (
    <div className="llm__pricing section__padding" id="pricing">
      <div className="llm__pricing-heading">
        <h1>Pricing</h1>
      </div>
      <div className="llm__pricing-content">
        <div className="llm__pricing-content-heading">
          <h2>Choose a Plan That Fits Your Needs</h2>
          <h2>Flexible Pricing Options for Businesses of All Sizes</h2>
        </div>
        <div className="llm__pricing-content-plans">
          <div className="llm__pricing-content-plans-starter">
            <h2>Free</h2>

            <p>14 days trial</p>
            <p>Generate up to 20 reports</p>
            <div className="llm__pricing-content-plans-starter-tick">
              <p>Admin dashboard</p>
              <p>Customizable reports</p>
              <p>Reports download</p>
              <p>Cloud storage </p>
            </div>

            <a href="http://main.episum.com/register/form">
              <button type="button">Create Free</button>
            </a>
          </div>

          {/* <div className="llm__pricing-content-plans-pro">
            <h2>Pro</h2>
            <button type="button">Create Free</button>
          </div> */}
          {/* <div className="llm__pricing-content-plans-teamPlans">
            <h2>Team Plans</h2>
            <button type="button">  Let's Talk   </button>
          </div> */}
        </div>
      </div>
      <div className="llm__pricing-content-toggle">
        <button
          className={`toggle-button ${isYearly ? "yearly" : "monthly"}`}
          onClick={handleToggle}
        >
          {isYearly ? "Yearly Plan" : "Monthly Plan"}
        </button>
      </div>

    </div>
  );
};

export default Pricing;
